//Checks to see if the token given by the user is legitimate
this.pool = require('../database/DBPool');
this.parser = require('json-parser');


//Checks the user token
exports.checkToken = function(token, cb){
	var query = "select * from users where activititoken = \'" + token + "\'";
	this.pool.sendQuery(query, function(response){ //Queries db to see if token is there
		console.log(response);
		if(response.length < 1 ){
			console.log("User not found");
			cb(false);
		}
		else{
			console.log(response);
			cb(true);
		}
	});
}
